        axios.delete(`https://6481ae1529fa1c5c5031de07.mockapi.io/fake_book/${id}`)
            .then(() => {
                getData();
            })
    }
    return (
        <div className="container home">
        <AddBook updateData={updateData} onSubmit={onSubmit} setBook_name={setBook_name} setAuthor_name={setAuthor_name}/>
