import React, { Component } from 'react'

export default class Home extends Component {
 
        state = {
          book_name: '',
          author_name: ''
        }

        // add = (e) => {
        //     e.preventDefault();
        //     this.props.addContact(this.state);
        //     this.setState({
        //       name:"",
        //       email:""
        //     });
        //   }
    
      onSubmit(e) {
        e.preventDefault()
        console.log(`Name: ${this.state.book_name}`);
        console.log(`Email: ${this.state.author_name}`);
        this.setState({
            book_name:"",
            author_name:""
        });
      }
  render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-4 add_book">
                        <br/>
                        <form onSubmit={this.onSubmit}>
                            <div class="mb-3">
                                <label class="form-label">Book Title</label>
                                <input type="text" className="form-control" value={this.state.book_name} onChange={(e) => this.setState({ book_name: e.target.value })}/>
                            </div>
                            <div class="mb-3">
                                <label class="form-label">Author Name</label>
                                <input type="text" class="form-control" value={this.state.author_name} onChange={(e) => this.setState({ author_name: e.target.value })}/>
                            </div>
                            <div class="mb-3">
                            <button type="button" class="btn btn-success">Add book</button>
                            </div>
                        </form>
                    </div>

                    <div className="col-md-8 view_book">
                        <div className="row main_row">
                            <div className="book_image"></div>
                            <div className="book_details">
                                <h5>Book of c++</h5>
                                <div className="row sub_row">
                                    <div className="col-4">
                                        <label htmlFor=""><h6>Book Type :</h6></label>
                                        <br /><div className="data">story</div>
                                    </div>
                                    <div className="col-4">
                                        <label htmlFor=""><h6>No of pages :</h6></label>
                                        <br /><div className="data">289</div>
                                    </div>
                                    <div className="col-4">
                                        <label htmlFor=""><h6>Ratings :</h6></label>
                                        <br /><div className="data">* * * * *</div>
                                    </div>
                                </div>
                                <h6>Author: </h6>
                                
                            </div>
                        </div>
                        
                    </div>
                </div>
            </div>
        );
  }
}
