// 
import axios from 'axios';
import React, { useState, useEffect } from 'react'

export default function Home() {
    const [book_name, setBook_name] = useState('');
    const [author_name, setAuthor_name] = useState('');
    const [API_data, setAPI_data] = useState([]);
    useEffect(() => {
        axios.get(`https://6481ae1529fa1c5c5031de07.mockapi.io/fake_book`)
            .then((response) => {
                setAPI_data(response.data);
            })
    }, [])
    const onSubmit = (e) => {
        e.preventDefault();
        axios.post(`https://6481ae1529fa1c5c5031de07.mockapi.io/fake_book`, {
            book_name,
            author_name
        })
        setBook_name('');
        setAuthor_name('');
    }
    const setData = (data) => {
        setBook_name(data.book_name);
        setAuthor_name(data.author_name);
        setBook_id(data.id);
        document.getElementById('update').style.display = 'block';
        document.getElementById('add').style.display = 'none';
    }
    const updateData = (id) => {
        axios.put(`https://6481ae1529fa1c5c5031de07.mockapi.io/fake_book/${id}`, {
            book_name,
            author_name
        })
        document.getElementById('update').style.display = 'none';
        document.getElementById('add').style.display = 'block';
        
    }
    const deleteData = (id) => {
        axios.delete(`https://6481ae1529fa1c5c5031de07.mockapi.io/fake_book/${id}`)
        .then(() => {
            // window.location.reload();
        })
    }


    return (
        <div className="container home">
            <div className="add_book">
                <br />
                <form onSubmit={onSubmit}>
                    <div className="mb-3">
                        <label className="form-label">Book Title</label>
                        <input type="text" className="form-control" value={book_name} onChange={(e) => setBook_name(e.target.value)} />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Author Name</label>
                        <input type="text" className="form-control" value={author_name} onChange={(e) => setAuthor_name(e.target.value)} />
                    </div>
                    <div className="mb-3">
                        <input type="submit" className="btn btn-success" value="Add Book" id='add' />
                        <input type="button" className="btn btn-success" value="Update Book" id='update' style={{ 'display': 'none' }} onClick={() => { updateData() }} />
                    </div>
                </form>
            </div>

            <div className="view_book">
                {
                    API_data.map((data) => {
                        return (
                            <div className="row main_row">
                                <div className="book_image"></div>
                                <div className="book_details">
                                    <h5><span className="book_name">{data.book_name}</span><span className="buttons">
                                        <i className='far fa-edit' onClick={() => { setData(data) }}></i>
                                        <i className='far fa-trash-alt'onClick={() => { deleteData(data.id) }}></i>
                                    </span></h5>
                                    <div className="row sub_row">
                                        <div className="col-4">
                                            <label htmlFor=""><h6>Book Type</h6></label>
                                            <br /><div className="data">story</div>
                                        </div>
                                        <div className="col-4">
                                            <label htmlFor=""><h6>No of pages</h6></label>
                                            <br /><div className="data">289</div>
                                        </div>
                                        <div className="col-4">
                                            <label htmlFor=""><h6>Ratings</h6></label>
                                            <br /><div className="data">* * * * *</div>
                                        </div>
                                    </div>
                                    <h6>Author: {data.author_name} </h6>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}
